(function(a){var b=window.location.href,c=a("h1").text();a(".social-icons-to-share-in-facebook").bind("click",function(){window.open("https://www.facebook.com/sharer/sharer.php?u="+b,"facebook-share-dialog")}),a(".social-icons-to-share-in-twitter").bind("click",function(){window.open("https://twitter.com/intent/tweet?text="+c+"&url="+b)}),a(".social-icons-to-share-in-linkedin").bind("click",function(){window.open("https://www.linkedin.com/shareArticle?mini=true&url="+b+"&title="+c)}),a(".social-icons-to-share-in-email").bind("click",function(){window.location="mailto:?&subject="+c+"&body="+b})})(jQuery);